import org.jpos.iso.ISOMsg;
import org.jpos.iso.ISOUtil;
import org.jpos.iso.packager.GenericPackager;

/**
 * @author yinheli
 */
public class Test {
    public static void main(String[] args) throws Exception {
        byte[] data = ISOUtil.hex2byte("");

        ISOMsg m = new ISOMsg();
        m.setPackager(new GenericPackager("D:\\develop\\workspace\\wanda\\pos-adapter\\cfg\\packager\\pos-adapter.xml"));
        m.unpack(data);
        m.dump(System.out, "");

        System.out.println("f60->" + new String(m.getBytes(60), "gbk"));
        System.out.println("f61->" + new String(m.getBytes(61), "gbk"));
        System.out.println("f63->" + new String(m.getBytes(63), "gbk"));
    }
}
